
public class For_Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i=0; i<=3; i++)
{
	System.out.println("Value of i is---> "+i );
}

System.out.println("....");
int k=200;
for(int j=3; j>=0; j--)
{
	System.out.println("Value of j is --->" +j);
	k= k-10;
	
}
System.out.println("");
System.out.println("Value of k is ---->" +k);

	}

}
