
public class If_Statement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int i = 49;
int j=  50;
//int k=  75;


System.out.println("");
System.out.println("***If Statement Example***");

if (i<j)
System.out.println("Value of i("+i+") is smaller than value of j("+j+")");

// if else statement

System.out.println("");
System.out.println("***If Else Statement Example***");

 if (i<j)
	 System.out.println("Value of i("+i+" is smaller than value of j("+j+")");
 else 
	 System.out.println("Value of j("+j+" is smaller than value of i("+i+")");
	}

	//Nested if else statement
	
}
